\n\r\n//Editor .InsertHtml(img_htm); \r\n\r\nEditor .insertHtml(img_htm); \r\n}\r\n\r\n\r\n\r\n	} catch (ex) {\r\n		//this.debug(ex);\r\n	}\r\n}\r\n\r\n\r\n\r\n \r\n\r\n\r\n\r\n\r\n})();\r\n\r\n	\r\n\r\n\r\n</script>\r\n\r\n\r\n\r\n	\r\n	\r\n\r\n<span id=\"spanButtonPlaceholder_<?php $CI = &get_instance();echo $CI->Common_model->get_uid();?>\"></span>\r\n\r\n<input id=\"btnUpload\" type=\"button\" value=\"上传\" style=\"width: 80px; height: 20px; \" />		\r\n<input type=\"hidden\" name=\"%name\" id=\"pic_<?php $CI = &get_instance();echo $CI->Common_model->get_uid();?>\" value=\"%value\" />\r\n<input id=\"btnSelect_<?php $CI = &get_instance();echo $CI->Common_model->get_uid();?>\" type=\"button\" value=\"从图库选择\" style=\"width: 80px; height: 20px; \" onclick=\"createFinder(this,\'<?php $CI = &get_instance();echo $CI->Common_model->get_uid();?>\');\"  />\r\n<script type=\"text/javascript\">\r\nfunction createFinder(obj,uid){\r\nvar finder = new CKFinder();\r\nfinder.baseUrl = url_root+\"swfupload/uploads/\";\r\nfinder.resourceType = \'Images\';\r\nfinder.selectActionFunction = function( fileUrl, data ) {	\r\n	// Using CKFinderAPI to show simple dialog.	\r\n	\r\n\r\n$.ajax({\r\n			   type: \"POST\",\r\n			   url: site_url +\"/backend/common/ajax_imgid_get\",\r\n			   data: {i_url:encodeURIComponent(fileUrl.substr(fileUrl.lastIndexOf(\'/\')))},\r\n                           async:false,\r\n			   success: function(msg){\r\n\r\n			   	\r\nvar input_hid = $(\"#pic_\"+uid);\r\nvar sh  =input_hid.val()?input_hid.val().split(\',\'):new Array();\r\n\r\n\r\nsh.push(msg);\r\n\r\n\r\ninput_hid.val($.unique(sh).join(\',\'));\r\n\r\n			     	\r\nvar img_htm = \'<img src=\"\'+fileUrl+\'\" /><a href=\"\'+fileUrl+\'\" class=\"hide\" target=\"_blank\" ></a>\';\r\n\r\n\r\n$(\'#pic_prev_\'+uid ).html($(\'#pic_prev_\'+uid ).html()+img_htm);\r\n\r\n\r\njqfn($(\'#pic_prev_\'+uid ));\r\n\r\n\r\nvar Editor = CKEDITOR.instances[\"main_p_desc_\"];\r\n\r\n\r\nif(typeof(Editor)!=\"undefined\"){\r\nEd